<script>
var year = [2021,2022,2023,2024,2025];

console.log(year);

let currentLocale = 'en';

if (currentLocale === 'th') {
    year = year.map(y => typeof y === "number" ? y + 543 : y);  // Add 543 for Thai year
} else {
    // For English or other languages, leave the year as is
    year = year.map(y => typeof y === "number" ? y : y);
}

console.log(year);

var paper_tci = [0,0,0,0,0];
var paper_scopus = [42,16,0,0,0];
var paper_wos = [0,1,0,0,0];

var paper_tci_cit = [0,0,0,0,0];
var paper_scopus_cit = [47,2,0,0,0];
var paper_wos_cit = [0,0,0,0,0];

// year.unshift("source");
year.unshift("Source");
paper_tci.unshift("tci");

//console.log(paper_scopus_cit);
paper_scopus.unshift("scopus");
paper_wos.unshift("wos");

let pro = year;
let dat = [
    paper_scopus,
    paper_tci,
    paper_wos
];
let res = dat.map((innerArray) => {
    let obj = {};
    innerArray.forEach((innerData, index) => {
        //index.hasOwnProperty("undefined");
        // if(pro[index]==undefined){
        //     obj['source'] = innerData;
        //     console.log(obj)
        // }else{
        //     obj[pro[index]] = innerData;
        // }
        obj[pro[index]] = innerData;

        //console.log(pro[index])
    });

    return obj;
});

console.log(res);

//let props2 = year;

paper_scopus_cit.unshift("scopus");
//console.log(paper_scopus_cit);
paper_tci_cit.unshift("tci");
paper_wos_cit.unshift("wos");


let dat2 = [
    paper_scopus_cit,
    paper_tci_cit,
    paper_wos_cit
];

let res2 = dat2.map((innerArray) => {
    let obj = {};
    innerArray.forEach((innerData, index) => {
        obj[pro[index]] = innerData;
    });
    return obj;
});
console.log(res2);

function generateTableHead(table, data) {
    let thead = table.createTHead();
    let row = thead.insertRow();
    for (let key of data) {
        let th = document.createElement("th");
        let text = document.createTextNode(key);
        th.appendChild(text);
        row.appendChild(th);
    }
}

function generateTable(table, data) {
    for (let element of data) {
        let row = table.insertRow();
        for (key in element) {
            let cell = row.insertCell();
            let text = document.createTextNode(element[key]);
            cell.appendChild(text);
        }
    }
}

let table = document.getElementById("myTable");
let data = Object.keys(res[0]);
generateTableHead(table, data);
generateTable(table, res);
let table2 = document.getElementById("myTable2");
let data2 = Object.keys(res2[0]);
generateTableHead(table2, data2);
generateTable(table2, res2);
</script>

<script>
var year = [2021,2022,2023,2024,2025];

if (currentLocale === 'th') {
